declare module "debug_console" {
    /**
     * 调试控制台
     */
    export namespace DebugConsole {
        /**
         * 最大消息数量
         */
        let maxMessageCount: number;
        /**
         * 开启调试控制
         * @param isMonitorServer
         * @effect 仅在客户端调用生效
         * ```
         * 是否监控服务器
         * *如果监控服务器RPC会增高
         * ```
         */
        function start(isMonitorServer: boolean): void;
        /**
         * 停止调试控制
         * @effect 仅在客户端调用生效
         */
        function stop(): void;
    }
}
